import React from "react";
import { Parallax } from "react-scroll-parallax";
import AnimateMe from "../animations/profile-animation";
import { ReactComponent as Shape1 } from "./img/shape-1.svg";
import { ReactComponent as Shape2 } from "./img/shape-2.svg";
import { ReactComponent as Shape3 } from "./img/shape-3.svg";
import { ReactComponent as Shape4 } from "./img/shape-4.svg";

const Hero = (props) => {
  return (
    <div id="hero" className="section section-hero">
      <Parallax
        className="shape shape--1"
        x={[-40, 40]}
        y={[-40, 40]}
        disabled={props.parallaxDisabled}
      >
        <Shape1 />
      </Parallax>
      <Parallax
        className="shape shape--2"
        y={[-20, 20]}
        disabled={props.parallaxDisabled}
      >
        <Shape2 />
      </Parallax>
      <Parallax
        className="shape shape--3"
        y={[0, 40]}
        x={[-20, 0]}
        disabled={props.parallaxDisabled}
      >
        <Shape3 />
      </Parallax>
      <Parallax
        className="shape shape--4"
        y={[-30, 30]}
        disabled={props.parallaxDisabled}
      >
        <Shape4 />
      </Parallax>
      <Parallax
        className="shape shape--5"
        y={[-40, 40]}
        x={[-40, 40]}
        disabled={props.parallaxDisabled}
      >
        <Shape4 />
      </Parallax>
      <div className="section-hero--copy">
        <p className="hero--jobTitle">Frontend Developer</p>
        <h1>Mary McGinty</h1>
        <p>
          A frontend developer based in Dublin. With over 15 years experience in
          a fast paced agency environment, I'm used to problem solving in a
          hurry, figuring out simple solutions to complex problems and working
          closely with clients.
        </p>
        <p>Please find some recent projects below.</p>
      </div>
      <div className="section-hero--image">
        <AnimateMe />
      </div>
    </div>
  );
};

export default Hero;
