import gsap from 'gsap'
import style from './phone.scss'
import phoneImage from './img/google-pixel-5.png'

const Phone = () => {
  const screens = ['urbana1.jpg', 'urbana2.jpg', 'urbana3.jpg', 'urbana4.jpg', 'urbana5.jpg']
  // let screen_divs = Array.prototype.slice.call(document.querySelectorAll('.phoneScreen_img'))
  // var reversed_images = screen_divs.reverse()
  let list = document.querySelectorAll('.phoneScreen_img') //NodeList
var ar = [].slice.call(list) //BOOM, returns an array. 


  return (
    <div className='wrapper-phone'>
      <div className='phone'>
        <img className='phone--pixel' src={phoneImage} />
        <div className='phone--slideshow'>
          {screens.map((value, index) => {
            return <img className='phoneScreen_img' key={index} src={'/assets/img/' + value} />
          })}
        </div>
      </div>

    </div>
  )
}

export default Phone
