import React from 'react'
import Parallax from 'parallax-js'

class Workpanel extends React.Component {
  constructor (props) {
    super(props)
    this.state = {}
    this.scene = React.createRef()
  }

	 componentDidMount () {
    this.parallax = new Parallax(this.scene)
  }

  componentWillUnmount () {
    this.parallax.disable()
  }

  render () {
    let alignClass = 'copyLeft'
    let copyAnimation = 'fade-up-right'
    let imgAnimation = 'fade-up-left'
    if (this.props.align === 'copy-left') {
      alignClass = 'copy-left'
      copyAnimation = 'fade-up-right'
      imgAnimation = 'fade-up-left'
    } else {
      alignClass = 'copy-right'
      copyAnimation = 'fade-up-left'
      imgAnimation = 'fade-up-right'
    }

    { /* Let's get our tabs array and convert teh elements to list items. */ }
    const tabs = this.props.tabs
    const listItems = tabs.map((tab) =>
      <li key={tab}>
        {tab}
      </li>
    )

    const renderShape = () => {
      if (this.props.shape === 'triangle') {
        return <div className={'shape shape--' + this.props.shape + ' rotating'} style={{ borderColor: this.props.color }} data-depth={this.props.depth} />
      } else if (this.props.shape === 'circle') {
        return <div className={'shape shape--' + this.props.shape + ' scaling'} style={{ backgroundColor: this.props.color }} data-depth={this.props.depth} />
      } else {
        return <div className={'shape shape--' + this.props.shape + ' rotating'} style={{ backgroundColor: this.props.color }} data-depth={this.props.depth} />
      }
    }

    const renderLink = () => {
      if (this.props.link) {
        return <a className='section-work--link' href={this.props.link}>Go to Project</a>
      }
    }

    return (
      <div className={'section section-work ' + alignClass}>
        <div className='section-work--image' data-aos={imgAnimation}>

          {renderShape()}

          <img src='http://via.placeholder.com/600x300' alt='placeholder' />
        </div>
        <div className='section-work--copy' data-aos={copyAnimation}><h1>{this.props.title}</h1>
          <p>{this.props.copy}</p>
          <ul className='tabs--skills'>{listItems}</ul>

          {renderLink()}

        </div>
      </div>
    )
  }
}
export default Workpanel
