const ParallaxCache = () => {
  const { parallaxController } = useController()

    useLayoutEffect(() => {
    const handler = () => parallaxController.update()
        window.addEventListener('load', handler)
        return () => window.removeEventListener('load', handler)
    }, [parallaxController])

    return null
};
