import React from 'react'
import { gsap } from 'gsap'
import { ReactComponent as Animation } from '../SVG/mise-wave.svg'

class AnimateMe extends React.Component {
  componentDidMount () {
    // set up eyes
    const leftEye = '[data-name=\'eye-left\']'
    const rightEye = '[data-name=\'eye-right\']'
    const leftLashes = '[data-name=\'lashes-left\']'
    const rightLashes = '[data-name=\'lashes-right\']'
    const brows = '[data-name=\'eyebrows\']'
    gsap.set(leftEye, { transformOrigin: '50% 50% 0', scale: 1 })
    gsap.set(rightEye, { transformOrigin: '50% 50% 0', scale: 1 })

    // animate eyes
    const tl_eyes = gsap.timeline({ repeat: -1, repeatDelay: 3 })
    // down
    tl_eyes.to(leftEye, { scaleY: 0.2, duration: 0.1 }, 'blink_down')
    tl_eyes.to(rightEye, { scaleY: 0.2, rotation: -9, duration: 0.1 }, 'blink_down')
    tl_eyes.to(rightLashes, { scaleY: 0.2, rotation: 9, y: 13, duration: 0.1 }, 'blink_down')
    tl_eyes.to(leftLashes, { scaleY: 0.2, rotation: -9, y: 20, duration: 0.1 }, 'blink_down')
    // up
    tl_eyes.to(leftEye, { scaleY: 1, duration: 0.1 }, 'blink_up')
    tl_eyes.to(rightEye, { scaleY: 1, duration: 0.1 }, 'blink_up')
    tl_eyes.to(rightLashes, { scaleY: 1, rotation: 0, y: 0, duration: 0.1 }, 'blink_up')
    tl_eyes.to(leftLashes, { scaleY: 1, rotation: 0, y: 0, duration: 0.1 }, 'blink_up')

    // set up arms
    const leftArm = '[data-name=\'arm\']'
    gsap.set(leftArm, { transformOrigin: '0% 100% 0', scale: 1, rotation: 55, y: 30 })

    const tl_arms = gsap.timeline({ repeat: -1, repeatDelay: 2 })
    tl_arms.to(leftArm, { y: 0, rotation: 0 })
    tl_arms.to(leftArm, { rotation: 10, duration: 0.2, yoyoEase: 'power2.Out', repeat: 10 })
    tl_arms.to(leftArm, { y: 70, rotation: 35, duration: 0.3 })

    // animate head
    const head = '[data-name=\'head\']'
    gsap.set(head, { transformOrigin: '50% 50% 0', scale: 1 })

    const tl_head = gsap.timeline({ repeat: -1, repeatDelay: 6, yoyo: true })
    // down
    tl_head.to(head, { rotation: 5, duration: 0.5 })
    tl_head.to(head, { rotation: -5, duration: 0.5 }, '+=5')
  }

  render () {
    return (
      <div className='anim-wave'><Animation /></div>
    )
  }
}

export default AnimateMe
