import React from 'react'
import gsap from 'gsap';
import {ReactComponent as Animation} from './mise.svg';

class AnimateMe extends React.Component{
	componentDidMount(){
		//set up eyes
var leftEye = `[data-name='eye-left']`;
var rightEye = `[data-name='eye-right']`;
var leftLashes = `[data-name='lashes-left']`;
var rightLashes = `[data-name='lashes-right']`;
var brows = `[data-name='eyebrows']`;
gsap.set(leftEye,{transformOrigin: "50% 50% 0", scale:1});
gsap.set(rightEye,{transformOrigin: "50% 50% 0", scale:1});


//animate eyes
var tl_eyes = gsap.timeline({repeat: -1, repeatDelay:3});
//down
tl_eyes.to(leftEye, {scaleY: 0.2, duration: 0.1});
tl_eyes.to(rightEye, {scaleY: 0.2, rotation:-9, duration: 0.2}, "-=0.1");
tl_eyes.to(rightLashes, {scaleY: 0.2, rotation:9, y:13, duration: 0.2}, "-=0.2");
tl_eyes.to(leftLashes, {scaleY: 0.2, rotation:-9, y:20, duration: 0.2}, "-=0.3");
tl_eyes.to(brows, {y: -5, duration: 0.1});
//up
tl_eyes.to(leftEye, {scaleY: 1, duration: 0.1});
tl_eyes.to(rightEye, {scaleY: 1, duration: 0.2}, "-=0.1");
tl_eyes.to(rightLashes, {scaleY: 1, rotation:0, y:0, duration: 0.2}, "-=0.1");
tl_eyes.to(leftLashes, {scaleY: 1, rotation:0, y:0, duration: 0.2}, "-=0.2");

//set up arms
var leftArm = `[data-name='arm-l']`;
var rightArm = `[data-name='arm-r']`;
var armsShoulders = `[data-name='arms']`;
gsap.set(leftArm,{transformOrigin: "0% 50% 0", scale:1, rotation:-10, x:3});
gsap.set(rightArm,{transformOrigin: "90% 50% 0", scale:1, rotation:-10, x:-3});

var tl_arms = gsap.timeline({repeat: -1, yoyo:true});
tl_arms.to(leftArm, {rotation:10, duration:0.1});
tl_arms.to(rightArm, {rotation:10, duration:0.1}, "-=0.1");
tl_arms.to(armsShoulders, {y:3, duration:0.1}, "-=0.1");
	}
	render(){

		return(
			<div id='svg-wrapper'>=<Animation /></div>
			)
	}
}


export default AnimateMe
