import React from 'react';

class Workpanel extends React.Component{
	render(){

		{/* set className on wrapper div for aligning left or right */}
		{/* determine which AOS animate class to apply do div comes from left / right */}

		let alignClass = "copyLeft"; 
		let copyAnimation = "fade-up-right";
		let imgAnimation = "fade-up-left";
		if(this.props.align ==='copy-left'){
			alignClass = "copy-left";
			copyAnimation = "fade-up-right";
			imgAnimation = "fade-up-left";
		}else{
			alignClass = "copy-right";
			copyAnimation = "fade-up-left";
			imgAnimation = "fade-up-right";
		}


		{/* Let's get our tabs array and convert teh elements to list items. */}
		const tabs = this.props.tabs;
		const listItems = tabs.map((tab) => <li key={tab}>{tab}</li>);


		const renderLink = () => {
		          if(this.props.link){
		            return <a className="section-work--link" href={this.props.link}>Go to Project</a>

		          } 
		        }

		return(
			<div className={'section section-work ' + alignClass}>
				<div className="section-work--dark section-work--image" data-aos={imgAnimation}><img src="http://via.placeholder.com/600x300" alt="placeholder" /></div>
				<div className="section-work--light section-work--copy" data-aos={copyAnimation}><h1>{this.props.title}</h1>
				<p>{this.props.copy}</p>
				<ul className="section-work--tabs">{listItems}</ul>

				{renderLink()}

				</div>
			</div>
			)
	}
}
export default Workpanel;
