import React from 'react'
import { ParallaxProvider, Parallax } from 'react-scroll-parallax'

class Workpanel extends React.Component {
  render () {
    let alignClass = 'copyLeft'
    let copyAnimation = 'fade-up-right'
    let imgAnimation = 'fade-up-left'
    if (this.props.align === 'copy-left') {
      alignClass = 'copy-left'
      copyAnimation = 'fade-up-right'
      imgAnimation = 'fade-up-left'
    } else {
      alignClass = 'copy-right'
      copyAnimation = 'fade-up-left'
      imgAnimation = 'fade-up-right'
    }

    { /* Let's get our challenges array and convert the elements to list items. */ }
    let challengeItems
    if (this.props.challenges) {
      const challenges = this.props.challenges
      challengeItems = challenges.map((challenge) =>
        <li key={challenge}>
          {challenge}
        </li>
      )
    }

    { /* Let's get our tabs array and convert the elements to list items. */ }
    let listItems
    if (this.props.tabs) {
      const tabs = this.props.tabs
      listItems = tabs.map((tab) =>
        <li key={tab}>
          &nbsp;{tab}
        </li>
      )
    }

    const renderLink = () => {
      if (this.props.link) {
        return <a className='section-work--link btn' href={this.props.link} rel='nofollow'>Go to Project</a>
      }
    }

    return (
      <div className={'section section-work ' + alignClass}>

        <div className='section-work--image'>
          <ParallaxProvider>
            {/* <Parallax className='work-image--bg' y={[-80, 80]} tagOuter='figure'>
              <img src='/assets/img/urb-reception.jpg' alt='placeholder' />
            </Parallax>
            <Parallax className='work-image--fg' y={[-40, 40]} tagOuter='figure'>
              <img src='/assets/img/urb-reception.jpg' alt='placeholder' />
            </Parallax> */}
            <ParallaxBanner
              className='your-class'
              layers={[
                {
                  image: '/assets/img/urb-reception.jpg',
                  amount: 0.1
                },
                {
                  image: '/assets/img/urb-reception.jpg',
                  amount: 0.2
                }
              ]}
              style={{
                height: '500px'
              }}
            >
              <h1>Banner Children</h1>
            </ParallaxBanner>
          </ParallaxProvider>

        </div>
        <div className='section-work--copy' data-aos={copyAnimation}><h2 style={{ color: this.props.color }}>{this.props.title}</h2>
          <p>{this.props.copy}</p>

          {challengeItems && <p>The key challenges for this project included:</p>}

          <ul className='tabs--challenges'>{challengeItems}</ul>
          <ul className='tabs--skills'>{listItems}</ul>

          {renderLink()}

        </div>
      </div>
    )
  }
}
export default Workpanel
