import React from 'react'
import { gsap } from 'gsap'
import { ReactComponent as Animation } from '../SVG/mise-wave.svg'

class AnimateMe extends React.Component {
  componentDidMount () {
    // set up eyes
    const leftEye = "[data-name='wave-eye-left']";
    const rightEye = "[data-name='wave-eye-right']";
    const rightLashes = "[data-name='wave-lashes-right']";
    gsap.set(leftEye, { transformOrigin: "50% 50% 0", scale: 1 });
    gsap.set(rightEye, { transformOrigin: "50% 50% 0", scale: 1 });

    // animate eyes
    const tl_eyes = gsap.timeline({ repeat: -1, repeatDelay: 3 });

    // down
    tl_eyes.to(leftEye, { scaleY: 0.2, duration: 0.1 }, "blink_down");
    tl_eyes.to(rightEye, { scaleY: 0.2, duration: 0.1 }, "blink_down");
    tl_eyes.to(
      rightLashes,
      { scaleY: 0.2, y: 10, duration: 0.1 },
      "blink_down"
    );
    // up
    tl_eyes.to(leftEye, { scaleY: 1, duration: 0.1 }, "blink_up");
    tl_eyes.to(rightEye, { scaleY: 1, duration: 0.1 }, "blink_up");
    tl_eyes.to(rightLashes, { scaleY: 1, y: 0, duration: 0.1 }, "blink_up");

    // set up arms
    const leftArm = "[data-name='wave-arm']";
    gsap.set(leftArm, {
      transformOrigin: "0% 100% 0",
      scale: 1,
      rotation: 0,
      y: 150,
    });

    const tl_arms = gsap.timeline({ repeat: -1, repeatDelay: 5 });
    tl_arms.to(leftArm, { y: 0, rotation: 0, duration: 0.15 });
    tl_arms.to(leftArm, {
      rotation: 10,
      duration: 0.15,
      yoyoEase: "power2.Out",
      repeat: 10,
    });
    tl_arms.to(leftArm, { y: 150, duration: 0.2 });

    // animate head
    const head = "[data-name='wave-head']";
    const head_bg = "[data-name='wave-head-bg']";
    gsap.set(head, { transformOrigin: "50% 50% 0", scale: 1 });
    gsap.set(head_bg, { transformOrigin: "50% 50% 0", scale: 1 });
    const tl_head = gsap.timeline({ repeat: -1, repeatDelay: 6, yoyo: true });
    // down
    tl_head.to(head, { rotation: 5, duration: 0.5 }, "head_tilt_1");
    tl_head.to(head_bg, { rotation: 5, duration: 0.5 }, "head_tilt_1");
    tl_head.to(head, { rotation: -5, duration: 0.5, delay:2 }, "head_tilt_2");
    tl_head.to(head_bg, { rotation: -3, duration: 0.5, delay:2 }, "head_tilt_2");
  }

  render () {
    return (
      <div className='anim-wave'><Animation /></div>
    )
  }
}

export default AnimateMe
