import React from 'react';

class Workpanel extends React.Component{
	constructor(props){
		super(props);
	}
	render(){

		{/* set className on wrapper div for aligning left or right */}
		const alignClass = "copyLeft"; 
		this.props.align =='copy-left' ? alignClass = "copyLeft" : alignClass = "copyRight";

		{/* Let's get our tabs array and convert teh elements to list items. */}
		const tabs = this.props.tabs;
		const listItems = tabs.map((tab) => <li>{tab}</li>);

		return(
			<div className={'section section-work ' + alignClass}>
				<div className="section-work--dark section-work--image"></div>
				<div className="section-work--light section-work--copy"><h1>{this.props.title}</h1>
				<p>{this.props.copy}</p>
				<ul className="section-work--tabs">{listItems}</ul>
				<a className="section-work--link" href="https://unislim.com">Go to Project</a>
				</div>
			</div>
			)
	}
}
export default Workpanel;
