import React, { useEffect, useState } from 'react'
import Workpanel from './work-panel'

const Work = () => {
  const [windowWidth, setWindowWidth] = useState(0)
  const [windowHeight, setWindowHeight] = useState(0)
  const [parallaxDisabled, setParallax] = useState(false)
  const resizeWindow = () => {
    setWindowWidth(window.innerWidth)
    setWindowHeight(window.innerHeight)

    if (window.innerWidth <= 500) {
      setParallax(true)
    } else {
      setParallax(false)
    }
  }

  useEffect(() => {
    resizeWindow()
    window.addEventListener('resize', resizeWindow)
    return () => window.removeEventListener('resize', resizeWindow)
  }, [])
  return (
    <div id='work'>

      <Workpanel
        color='#F8E0DE'
        bgColor='#2D3548'
        align='copy-right'
        title='Urbana'
        id='urbana'
        device='phone'
        parallaxDisabled={parallaxDisabled}
        bgImage='urb-reception.jpg'
        deviceImages={['urbana1.jpg', 'urbana2.jpg', 'urbana3.jpg', 'urbana4.jpg', 'urbana5.jpg']}
        copy='Urbana customers can now book, modify and cancel their appointments online, all within a simple to use interface. The system is fully integrated with the Salons appointment software provided by Phorest, reducing the time employees need to spend handling appointment bookings leading to a significant decrease in missed and cancelled appointments.'
        challenges={[
          'Distilling a complex processing requirement into a simple user journey',
          'Integrating with the Phorest API to create, modify, cancel and check in appointments',
          'A Twilio login system, using mobile phone number verification',
          'Developing a waiting list system where users can request to be added to a waiting list for certain time periods and repeating time periods. Staff can view and filter waiting list to match cancellations to waiting list clients. Leads lost/won are tracked.'
        ]}
        tabs={['UX testing', 'UX research', 'Wireframing', 'Design', 'Development', 'API Integration (Phorest & Twilio)']}
        link='https://unislim.com'
      />

      <Workpanel
        color='#55C887'
        bgColor='#28493D'
        align='copy-left'
        title='Dublin Zoo'
        id='zoo'
        device='laptop'
        parallaxDisabled={parallaxDisabled}
        bgImage='zoo-orangutan.jpg'
        deviceImages={['zoo1.jpg', 'zoo2.jpg', 'zoo4.jpg', 'zoo5.jpg']}
        copy="It was a pleasure to work with one of Ireland's National Institutions on the development of a new website. Priority here was on flexibility of content for the team internally, so part of my responsibility here was to develop a custom pagel panel building system to suit their specific content requirements."
        challenges={['Builing a flexible page builder system to allow client maximum content flexibility with a range of predefined panels.',
          'An events widgets with a complex set of filtering parameters.']}
        tabs={['Development']}
        link='https://dublinzoo.ie'
      />

      <Workpanel
        color='#EE9A50'
        bgColor='#011E41'
        align='copy-right'
        title='Marlet'
        id='marlet'
        device='phone'
        parallaxDisabled={parallaxDisabled}
        bgImage='marlet-city.jpg'
        deviceImages={['marlet1.jpg', 'marlet2.jpg', 'marlet3.jpg', 'marlet4.jpg']}
        copy='Meeting a brief that demanded a high speed website that was also rich in high quality imagery and video was a good learning experience with great results.'
        challenges={[
          'Striking an aceptable balance between page speed results and high quality image / video presentation',
          'A page builder system allowing maximum flexibility for the client, making sure that any combination of panels works and looks well',
          'Google maps implementation with custom styling, client can choose from a library of custom pins when adding projects, or upload fresh icons easily. '
        ]}
        tabs={['Development', 'Google Page Speed optimisation', 'API Integration (Google Maps Customisation)']}
        link='https://unislim.com'
      />

      <Workpanel
        color='#D1B8BB'
        bgColor='#6D1227'
        align='copy-right'
        title='Pacman'
        id='pacman'
        device='laptop'
        parallaxDisabled={parallaxDisabled}
        bgImage='pacman-sandtimer.jpg'
        deviceImages={['pacman1.jpg', 'pacman2.jpg', 'pacman3.jpg', 'pacman4.jpg']}
        copy='Development of a private internal company tool to track project spend, supplier quotes, client quotes, PM assignment and more. Content customised per user role to protect sensitive data. Integrates with the Timely API for time tracking to compare against costs'
        challenges={[
          'Reasearching and implementing the most efficient way to present and handle data. (Datatables.js)',
          'Requirements for particular filtering and sorting options',
          'Limiting data presented by user role',
          'Integration with external API (Timely) to consolidate time tracking software data with quotes costs'
        ]}
        tabs={['Development', 'Datatables.js', 'SQL']}
        link='https://unislim.com'
      />

    </div>

  )
}

export default Work
