import React, { useEffect } from 'react'
import gsap from 'gsap'
import style from './phone.scss'
import phoneImage from './img/google-pixel-5.png'
import { _sortPropTweensByPriority } from 'gsap/gsap-core'

const Phone = props => {
  useEffect(() => {
    const list = document.querySelectorAll('.phoneScreen_img' + props.id) // NodeList
    const ar = [].slice.call(list) // BOOM, returns an array.
    const reverse_ar = ar.reverse()
    const tl = gsap.timeline({ repeat: -1, repeatDelay: 0, onComplete: fadeinFirstScreen })
    tl.to(reverse_ar, {
      opacity: 0,
      duration: 1,
      stagger: 1, // space each element's animation out by 0.5 seconds
      delay: 1
    })
    const fadeinFirstScreen = function () {
      gsap.set(arr[0], { opacity: 1 })
    }
  })

  let deviceImageArray
  if (props.deviceImages) {
    const images = props.deviceImages
    deviceImageArray = images.map((image) =>
      <img className={'phoneScreen_img' + props.id} key={image} src={'/assets/img/' + image} />
    )
  }
  deviceImageArray = deviceImageArray.reverse()
  return (
    <div className='wrapper-phone'>

      <div className='phone'>
        <img className='phone--pixel' src={phoneImage} />
        <div className='phone--slideshow'>

          {deviceImageArray}
        </div>
      </div>

    </div>
  )
}

export default Phone
