// import logo from './logo.svg';
import React from 'react'
import Header from './components/header'
import Hero from './components/hero'
import Work from './components/work'
import About from './components/about'
import Contact from './components/contact'
import $ from 'jquery'
import AOS from 'aos'
import color from 'jquery-color'

import './App.scss'
import 'aos/dist/aos.css'

class App extends React.Component {
  componentDidMount () {
    AOS.init({
      duration: 1000

    })

    document.addEventListener('aos:in:bgupdate', ({ detail }) => {
      console.log('fade to: ' + detail.dataset.color)
      // $('#scene-work').animate({ backgroundColor: detail.dataset.color }, 1000)
      // $('body').stop(0, 0)
      $('body').animate({
        backgroundColor: detail.dataset.color
      }, 1000)
    })
    document.addEventListener('aos:out:bgupdate', ({ detail }) => {
      console.log('animated out', detail)
      // $('body').stop(0, 0)
      $('body').animate({
        backgroundColor: '#FFFFFF'
      }, 1000)
    })
  }

  componentWillUnmount () {
    document.removeEventListener('aos:in:bgupdate', ({ detail }) => {
      console.log(detail, 'animation to display element with custom event started')
    })
    document.removeEventListener('aos:out:bgupdate', ({ detail }) => {
      console.log(detail, 'animation to display element with custom event started')
    })
  }

  render () {
    return (
      <div className='App'>
        {/* <Header /> */}
        <Hero />
        <Work />
        <About />
        <Contact />
      </div>
    )
  }
}

export default App
