import React from 'react';
import Workpanel from './work-panel';
class Work extends React.Component{
	constructor(props){
		super(props);
	}
	render(){
		return(
			<div>
			<Workpanel 
				align="copy-left" 
				title="Development of Unislim's online services." 
				copy="Including a rapid response solution to the effect of Covid 19 on their services. Assisted in UX testing, UX research analysis, design, and primarily responsible for development. Membership area of the website integrates with Unislim's base membershipt software and mobile app." 
				tabs={['UX testing', 'UX research', 'Design', 'Development', 'API Integration', 'Speed Enhancements', 'Social Meta tags']} 
				link="https://unislim.com"
			/>


			<Workpanel 
				align="copy-right" 
				title="Giving Urbana customers full control of their own appointments" 
				copy="Urbana customers can now book, modify and cancel their appointments online, all within a simple to use interface leading to a significant decrease in missed and cancelled appointemnts. The system is fully integrated with the Salons appointment software provided by Phorest, reducing the time employees need to spend handling appointment bookings." 
				tabs={['UX testing', 'UX research', 'Wireframing', 'Design', 'Development', 'API Integration (Phorest & Twilio)']} 
				link="https://unislim.com"
			/>


			<Workpanel 
				align="copy-left" 
				title="A new lease of life for the Zoo" 
				copy="It was a pleasure to work with one of Ireland's National Institutions on the development of a new website. Priority here was on flexibility of content for the team internally, so part of my responsibility here was to develop a custom pagel panel building system to suit their specific content requirements." 
				tabs={['Development']} 
				link="https://unislim.com"
			/>

			<Workpanel 
				align="copy-right" 
				title="A Premium Poperty portfolio with an emphasis on speed" 
				copy="Meeting a brief that demanded a high speed website that was also rich in high quality imagery and video was a good learning experience with great results." 
				tabs={['Development']} 
				link="https://unislim.com"
			/>


			</div>


			);
	}
}
export default Work;
