import React from 'react'
import AnimateMe from './profile-animation'
class Hero extends React.Component {
  render () {
    return (
      <div id='hero' className='section section-hero'>
        {/* <div className='section-hero--container trigger-bg-color' data-bg-color='#FFFFFF' data-aos='fade-down'> */}
        <div className='section-hero--copy'>
            <p className='hero--jobTitle'>Frontend Developer</p>
            <h1>Mary McGinty</h1>
            <p>A frontend developer based in Dublin.
              With over 10 years experience in a fast paced agency environment, I'm used to problem solving in a hurry, figuring out simple solutions to complex problems and working closely with clients.
            </p>
          </div>
        <div className='section-hero--image'>
            <AnimateMe />
          </div>

        {/* </div> */}

      </div>
    )
  }
}
export default Hero
