import React from 'react';
class About extends React.Component{
	render(){
		return(
			<div id="hero" className="section section-hero">
				<div className="section-hero--copy" data-aos="fade-down">
				<h1>About</h1></div>
				<div className="section-hero--image" data-aos="fade-up">
					<p>I am UI Designer/Developer with over 10 years experience in a fast paced digital agency environment. I love to solve problems, whether it be through clever design or clean code. Bringing creative ideas to life is what makes me tick.</p>
					<p>Originally trained in Visual Communications, I've since upskilled and now work as a UI designer and digital designer. In my current role I manage my own smaller scale projects and client base, as well as part of a team on larger projects. </p>
					<p>I'm currently working through an online full stack development course.</p>
					<p>Outside of working life, I love to break away from the screen completely and indulge in knitting, crochet and amateur dressmaking.</p>
				</div>
			</div>
			);
	}
}
export default About;
