import React from 'react'
import gsap from 'gsap'
import { ReactComponent as Animation } from '../SVG/mise.svg'

class AnimateMe extends React.Component {
  componentDidMount () {
    // set up eyes
    const leftEye = '[data-name=\'eye-left\']'
    const rightEye = '[data-name=\'eye-right\']'
    const rightLashes = '[data-name=\'lashes-right\']'
    gsap.set(leftEye, { transformOrigin: '50% 50% 0', scale: 1 })
    gsap.set(rightEye, { transformOrigin: '50% 50% 0', scale: 1 })

    // animate eyes
    const tl_eyes = gsap.timeline({ repeat: -1, repeatDelay: 3, paused: true });
    // down
    tl_eyes.to(leftEye, { scaleY: 0.2, duration: 0.1 }, 'blink_down')
    tl_eyes.to(rightEye, { scaleY: 0.2, duration: 0.1 }, 'blink_down')
    tl_eyes.to(rightLashes, { scaleY: 0.2, y: 5, duration: 0.1 }, 'blink_down')
    // up
    tl_eyes.to(leftEye, { scaleY: 1, duration: 0.1 }, 'blink_up')
    tl_eyes.to(rightEye, { scaleY: 1, duration: 0.1 }, 'blink_up')
    tl_eyes.to(rightLashes, { scaleY: 1, y: 0, duration: 0.1 }, 'blink_up')

    // set up arms
    const leftArm = '[data-name=\'arm-l\']'
    const rightArm = '[data-name=\'arm-r\']'
    const armsShoulders = '[data-name=\'arms\']'
    gsap.set(leftArm, { transformOrigin: '0% 50% 0', scale: 1, rotation: -10, x: 3 })
    gsap.set(rightArm, { transformOrigin: '90% 50% 0', scale: 1, rotation: -10, x: -3 })

    const tl_arms = gsap.timeline({ repeat: -1, yoyo: true })
    tl_arms.to(leftArm, { rotation: 10, duration: 0.1 })
    tl_arms.to(rightArm, { rotation: 10, duration: 0.1 }, '-=0.1')
    tl_arms.to(armsShoulders, { y: 3, duration: 0.1 }, '-=0.1')

    // animate head
    const head = '[data-name=\'head\']'
    gsap.set(head, { transformOrigin: '50% 50% 0', scale: 1 })

    const tl_head = gsap.timeline({ repeat: -1, repeatDelay: 6, yoyo: true })
    // down
    tl_head.to(head, { rotation: 5, duration: 0.5 })
    tl_head.to(head, { rotation: -5, duration: 0.5 }, '+=5')
  }

  render () {
    return (
      <div className='anim-type'><Animation /></div>
    )
  }
}

export default AnimateMe
