import React, { useEffect } from 'react'
import gsap from 'gsap'
import laptopImage from './img/macbook-air.svg'

const Laptop = props => {
  useEffect(() => {
    const list = document.querySelectorAll('.laptopScreen_img' + props.id) // NodeList
    const ar = [].slice.call(list) // BOOM, returns an array.
    const reverse_ar = ar.reverse()
    const tl = gsap.timeline({ repeat: -1, repeatDelay: 0, onComplete: fadeinFirstScreen, onCompleteParams: [reverse_ar] })
    tl.to(reverse_ar, {
      opacity: 0,
      duration: 0.5,
      stagger: 3, // space each element's animation out by 0.5 seconds
      delay: 4
    })
  })

  const fadeinFirstScreen = function (arr) {
    gsap.set(arr[0], { opacity: 1 })
  }

  let deviceImageArray
  if (props.deviceImages) {
    const images = props.deviceImages
    deviceImageArray = images.map((image) =>
      <img className={'laptopScreen_img' + props.id} key={image} src={'/assets/img/' + image} alt={props.id} />
    )
  }
  deviceImageArray = deviceImageArray.reverse()
  return (
    <div className='wrapper-laptop'>

      <div className='laptop'>

        <img className='laptop--macbook' src={laptopImage} alt='laptop' />
        <div className='laptop--slideshow'>
          <img className='laptop_img_bg' src={'/assets/img/' + props.deviceImages[0]} alt={props.id} />

          {deviceImageArray}
        </div>
      </div>

    </div>
  )
}

export default Laptop
