import React from 'react'
import { gsap } from 'gsap'
class Header extends React.Component {
  componentDidMount () {
    const mobileTrigger = document.getElementById('nav--mobile')
    gsap.set(mobileTrigger, { opacity: 0, width:0 })
  }

  render () {
    let menuActive = false
    function toggleMenu () {
      const element = document.getElementById('menu_toggle')
      element.classList.toggle('active')
      const mobileMenu = document.getElementById('nav--mobile')
      // menu.classList.toggle('active')
      if (menuActive === false) {
        gsap.to(mobileMenu, { opacity: 1 })
        menuActive = true
      } else {
        gsap.to(mobileMenu, { opacity: 0 })
        menuActive = false
      }
    }

    return (

      <div className='header' data-aos='nav-animation' data-aos-anchor-placement='top-top' data-aos-duration='500' data-aos-ease='ease-out-back' data-aos-delay='0'>
        <div className='logo'>mcg</div>
        <nav className='nav--desktop' role='navigation'>
          <div id='menu_toggle' class='menu_toggle' onClick={toggleMenu}>menu</div>
          <ul>
            <li><a href='#work'>work</a></li>
            <li><a href='#about'>about</a></li>
            <li><a href='#skills'>skills</a></li>
            <li><a href='#contact'>contact</a></li>
          </ul>
        </nav>

        <nav id='nav--mobile' className='nav--mobile' role='navigation'>
          <div className='logo'>mcg</div>
          <ul>
            <li><a href='#work'>work</a></li>
            <li><a href='#about'>about</a></li>
            <li><a href='#skills'>skills</a></li>
            <li>
              <a href='mailto:mry.mcginty@gmail.com'><img src='/assets/img/icon-email.svg' alt='Email Me' class='icon-email' /></a>
              <a href='https://www.linkedin.com/in/mrycollins/'><img src='/assets/img/icon-linked-in.svg' alt='Connect on Linked In' class='icon-linkedin' /></a>
            </li>
          </ul>
        </nav>

      </div>

    )
  }
}
export default Header
