import React, { useEffect, useState } from 'react'
import { Parallax } from 'react-scroll-parallax'
import Phone from '../phone/phone.js'
import Laptop from '../laptop/laptop.js'
const Workpanel = props => {
  const [windowWidth, setWindowWidth] = useState(0)
  const [windowHeight, setWindowHeight] = useState(0)
  const resizeWindow = () => {
    setWindowWidth(window.innerWidth)
    setWindowHeight(window.innerHeight)
  }

  useEffect(() => {
    resizeWindow()
    window.addEventListener('resize', resizeWindow)
    return () => window.removeEventListener('resize', resizeWindow)
  }, [])

  let alignClass = 'copyLeft'
  props.align === 'copy-left' ? alignClass = 'copy-left' : alignClass = 'copy-right'
  { /* Let's get our challenges array and convert the elements to list items. */ }
  let challengeItems
  if (props.challenges) {
    const challenges = props.challenges
    challengeItems = challenges.map((challenge) =>
      <li key={challenge}>
        {challenge}
      </li>
    )
  }

  { /* Let's get our tabs array and convert the elements to list items. */ }
  let listItems
  if (props.tabs) {
    const tabs = props.tabs
    listItems = tabs.map((tab) =>
      <li key={tab}>
        {tab}
      </li>
    )
  }

  const renderLink = () => {
    if (props.link) {
      return <a className='section-work--link btn' href={props.link} rel='nofollow'>Go to Project</a>
    }
  }
  let deviceMarkup
  if (props.device == 'phone') {
    deviceMarkup = <Phone id={props.id} device={props.device} deviceImages={props.deviceImages} />
  } else if (props.device == 'laptop') {
    deviceMarkup = <Laptop id={props.id} device={props.device} deviceImages={props.deviceImages} />
  }

  return (

    <div className={'section section-work ' + alignClass}>

      <div className='section-work--image'>
        <div />
        <Parallax className='work-image--bg' y={[0, 60]}>
          <img src={'/assets/img/' + props.bgImage} />
        </Parallax>
        <div className='work-image--circle' />
        <Parallax className='work-image--fg' y={[-40, 40]}>
          {deviceMarkup}

        </Parallax>

      </div>
      <div className='section-work--copy'><h2 style={{ color: props.color }}>{props.title}</h2>
        <p>{props.copy}</p>

      </div>

      <Parallax className='section-work--base' y={[0, 100]}>
        <div>
          {challengeItems && <p>The key challenges for this project included:</p>}

          <ul className='tabs--challenges'>{challengeItems}</ul>
          <ul className='tabs--skills'>{listItems}</ul>

          {renderLink()}
        </div>
      </Parallax>

    </div>
  )
}

export default Workpanel
